div.script:
// Conversion des hashs en paramètres de requête et filtrage
(function() {
    // Fonction pour convertir hash en paramètre de requête
    function convertHashToQueryParam() {
        const hash = window.location.hash.substring(1);
        if (hash && !window.location.search.includes('category=')) {
            // Si on a un hash mais pas de paramètre category, on redirige
            const newUrl = window.location.pathname + '?category=' + encodeURIComponent(hash);
            window.location.replace(newUrl);
            return true;
        }
        return false;
    }

    // Vérifier au chargement de la page
    if (convertHashToQueryParam()) {
        return;
    }

    // Intercepter les clics sur les liens avec hash
    document.addEventListener('click', function(e) {
        const link = e.target.closest('a');
        if (link && link.href) {
            const url = new URL(link.href);
            // Si le lien pointe vers /shop avec un hash
            if (url.pathname === '/shop' && url.hash && url.hash.length > 1) {
                e.preventDefault();
                const category = url.hash.substring(1);
                const newUrl = '/shop?category=' + encodeURIComponent(category);
                window.location.href = newUrl;
                return false;
            }
        }
    });

    // Filtrage et tri des produits
    document.addEventListener('DOMContentLoaded', function() {
        // Scroller vers le haut si une catégorie est sélectionnée
        const urlParams = new URLSearchParams(window.location.search);
        if (urlParams.get('category')) {
            window.scrollTo({ top: 0, behavior: 'smooth' });
        }

        // Gestion du tri des produits
        const sortSelect = document.getElementById('sort-products');
        if (sortSelect) {
            sortSelect.addEventListener('change', function() {
                const products = Array.from(document.querySelectorAll('.product-item'));
                const container = document.getElementById('products-grid');

                products.sort(function(a, b) {
                    const value = this.value;
                    if (value === 'name-asc') {
                        return a.dataset.name.localeCompare(b.dataset.name);
                    } else if (value === 'name-desc') {
                        return b.dataset.name.localeCompare(a.dataset.name);
                    } else if (value === 'price-asc') {
                        return parseFloat(a.dataset.price) - parseFloat(b.dataset.price);
                    } else if (value === 'price-desc') {
                        return parseFloat(b.dataset.price) - parseFloat(a.dataset.price);
                    }
                    return 0;
                }.bind(this));

                products.forEach(product => container.appendChild(product));
            });
        }
    });
})();
